import {AfterViewInit, Component, OnDestroy} from '@angular/core';
import * as particlesJS from 'particles.js';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements AfterViewInit, OnDestroy {

  constructor() { }

  ngAfterViewInit() {
    particlesJS.load('particles-js', "src/assets/particles.json", null);
  }

  ngOnDestroy() {
    // Cleanup any running particles.js instances to avoid memory leaks
    const instance = (window as any).pJSDom[0];
    if (instance) instance.pJS.fn.vendors.destroypJS();
  }
}
<app-navigation></app-navigation>
<div id="particles-js"></div>
